// import { useState } from "react";
import { useQuery } from "react-query";
import styled from "styled-components";
import { aiChat } from "../apis/api";

const ChatBox = styled.span`
  background: white;
  border: 1px solid rgb(209, 215, 237);
  border-radius: 8px;
  padding: 32px 38px 34px 31px;
  margin-bottom: 20px;
  gap: 30px;
  font-size: 16px;
  text-align: justify;
  align-items: flex-start;
`;

function Answer(question: any) {
    const { isLoading, data: answerData } = useQuery(
        ["aiChat"],
        async () => {
            const answer = await aiChat(question);
            console.log("쿼리요청됨", answer.message.result.translatedText)
            return answer.message.result.translatedText;
        },
    );
    return (
        isLoading ? <ChatBox>{answerData}</ChatBox> : null
    );
}

export default Answer;
