import { useState } from "react";
import { useQuery } from "react-query";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { MoreImages } from "../apis/api";

const Wrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
    width: 100%;
    font-family: "kcc";
    justify-content: center;
`;

const ImgBoxs = styled.div`
    display: flex;
    margin-top: 5vh;
    width: 90%;
    background-color: blue;
    justify-content: center;
`;

const Img = styled.img`
    width: 200px;
    height: 200px;
`;

interface Idata {
    // [key: number]: any,
    contentid: number,
    originimgurl: string,
    imgname: string,
    smallimageurl: string,
    cpyrhtDivCd: string,
    serialnum: string
}

const imgAnimation = {
    invisible: {
        x: 500,
        opacity: 0,
        scale: 0,
    },
    visible: {},
    exit: {},
}

function MoreImg() {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const contentId = Number(searchParams.get("id"));
    const { data: imgData } = useQuery<Idata[]>(
        ["img"], async () => {
            const data = await MoreImages(contentId);
            return data.response.body.items.item;
        }
    );
    const [show, setShow] = useState(0);
    const nextClick = () => setShow((prev) => (prev === 3 ? 3 : prev + 1))

    // console.log(imgData?.[0].originimgurl);
    return (
        <Wrapper>
            <ImgBoxs>
                {[0, 1, 2, 3].map((i) => (
                    i === show ? <Img key={i} src={imgData?.[i].originimgurl} /> : null
                ))}
            </ImgBoxs>
            <button onClick={nextClick}>next</button>
        </Wrapper>
    );
}

export default MoreImg;
