import styled from "styled-components";
import { useQuery } from "react-query";
import { areaTripIntro } from "../apis/api";

const Wrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    font-family: "kcc";
`;

const InfoTypeName = styled.div`
    font-size: 30px;
`;

interface Inum {
    contenttypeid?: number,
    contentId: number
}

function AreaTourIntro(props: Inum) {
    const type: object = { 12: "관광지", 14: "문화시설", 15: "행사/공연/축제", 25: "여행코스", 28: "레포츠", 32: "숙박", 38: "쇼핑", 39: "음식점" };

    const { isLoading, data: areaIntroData } = useQuery<any>(
        ["areaInfoData"],
        async () => {
            const areadata = await areaTripIntro(props.contentId, props.contenttypeid)
            // console.log("쿼리요청됨")
            return areadata.response.body.items.item[0]
        },
    );
    console.log(isLoading, areaIntroData, "쿼리요청")
    return (
        <Wrapper>
            {areaIntroData?.contenttypeid === 12
                ? <InfoTypeName>관광지</InfoTypeName>
                : areaIntroData?.contenttypeid === 14
                    ? <InfoTypeName>문화시설</InfoTypeName>
                    : areaIntroData?.contenttypeid === 15
                        ? <InfoTypeName>행사/공연/축제</InfoTypeName>
                        : areaIntroData?.contenttypeid === 25
                            ? <InfoTypeName>여행코스</InfoTypeName>
                            : areaIntroData?.contenttypeid === 28
                                ? <InfoTypeName>레포츠</InfoTypeName>
                                : areaIntroData?.contenttypeid === 32
                                    ? <InfoTypeName>숙박</InfoTypeName>
                                    : areaIntroData?.contenttypeid === 38
                                        ? <InfoTypeName>쇼핑</InfoTypeName>
                                        : areaIntroData?.contenttypeid === 39
                                            ? <InfoTypeName>음식점</InfoTypeName>
                                            : null
            }
        </Wrapper>
    );
}

export default AreaTourIntro;
