import { useState } from "react";
import { useQuery } from "react-query";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { MoreImages } from "../apis/api";

const Wrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    font-family: "kcc";
`;

const ImgBoxs = styled.div`
    font-size: 25px;
    margin-top: 20px;
    margin-bottom: 20px;
`;

const Img = styled.img`
    width: 200px;
    height: 200px;
`;

interface Idata {
    // [key: number]: any,
    contentid: number,
    originimgurl: string,
    imgname: string,
    smallimageurl: string,
    cpyrhtDivCd: string,
    serialnum: string
}


function MoreImg() {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const contentId = Number(searchParams.get("id"));
    const { data: imgData } = useQuery<Idata[]>(
        ["img"], async () => {
            const data = await MoreImages(contentId);
            return data.response.body.items.item;
        }
    );
    const [show, setShow] = useState(0);

    // console.log(imgData?.[0].originimgurl);
    return (
        <Wrapper>
            <ImgBoxs>
                {[0, 1, 2, 3].map((i) => (
                    i === show ? <Img src={imgData?.[i].originimgurl} /> : null
                ))}
            </ImgBoxs>
        </Wrapper>
    );
}

export default MoreImg;
