import { useState } from "react";
import { useQuery } from "react-query";
import styled from "styled-components";
import { aiChat } from "../apis/api";

const Wrapper = styled.div`
`;

const Banner = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 60px;
  background-size: cover;
`;

const ChatContent = styled.div`
  height: 70vh;
  overflow-y: scroll;
`;

const Line = styled.div`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  color: black;
`;

const ChatBox = styled.span`
  background: white;
  border: 1px solid rgb(209, 215, 237);
  border-radius: 8px;
  padding: 32px 38px 34px 31px;
  margin-bottom: 20px;
  gap: 30px;
  font-size: 16px;
  text-align: justify;
  align-items: flex-start;
`;


const ChatFormBox = styled.form`
  display: flex;
  background-color: rgb(242, 247, 255);
  border-top-left-radius: 8px;
  border-top-right-radius: 8px;
  opacity: 1;
`;

const ChatInputBox = styled.input`

    max-height: 100px;
`;

const ChatSendButton = styled.button`

`;


function Chat() {
  const [question, setQuestion] = useState("");
  const { isLoading, data } = useQuery(
    ["aiChat"],
    async () => {
      const answer = await aiChat(question);
      console.log("쿼리요청됨")
      return answer
    },
    {
      enabled: !!question,
    }
  );

  const sendSumit = (e: any) => {
    e.preventDefault();
    setQuestion(e.target[0].value)
  }

  return (
    <Wrapper>
      <Banner>
        <ChatContent>
          <Line>
            <ChatBox>어디가 궁금하신가요??</ChatBox>
            <ChatBox>{question}</ChatBox>
            {isLoading ?
              <ChatBox>{data}</ChatBox>
              : null}
          </Line>
        </ChatContent>
        <ChatFormBox onSubmit={sendSumit}>
          <ChatInputBox name="myText" />
          <ChatSendButton>
            보내기
          </ChatSendButton>
        </ChatFormBox>
      </Banner>
    </Wrapper>
  );
}

export default Chat;
